,p_display_name=>'IG - Set items'
,p_category=>'EXECUTE'
,p_supported_ui_types=>'DESKTOP'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'function render(',
'  p_dynamic_action in apex_plugin.t_dynamic_action',
', p_plugin         in apex_plugin.t_plugin) return apex_plugin.t_dynamic_action_render_result',
'is',
'  l_js            varchar2(4000);',
'  l_result        apex_plugin.t_dynamic_action_render_result;',
'  l_region_id     varchar2(100);',
'begin',
'  ',
'  -- Get the region id',
'  select coalesce(static_id, ''R'' || to_char(region_id)) into l_region_id',
'    from apex_application_page_regions',
'   where region_id = p_dynamic_action.triggering_region_id;',
'   ',
'  -- Add js files',
'  apex_javascript.add_library (',
'    p_name                    => ''IGSetItems#MIN#''',
'  , p_directory               => p_plugin.file_prefix',
'  , p_check_to_add_minified   => true',
'  );',
'',
'  -- Try to set an active record in the IG but only when active row = ''Y''',
'  if p_dynamic_action.attribute_04 = ''Y'' then',
'',
'    l_js :=',
'    q''[mho.IGSetItems.selectRecordWithPkPageItemValue({',
'      regionId: "#REGION_ID#",',
'      pkItem: "#PK_ITEM#"',
'    })]'';',
'    l_js := replace(l_js,''#REGION_ID#'',l_region_id);',
'    l_js := replace(l_js,''#PK_ITEM#'',p_dynamic_action.attribute_01);',
'',
'    -- Voeg subscription toe',
'    apex_javascript.add_onload_code(p_code => l_js);',
'',
'  end if;',
'',
'',
'  -- Op page load data binding toevoegen (en voor editable grids two way data binding)',
'  l_js :=',
'  q''[mho.IGSetItems.addDataBinding({',
'    regionId: "#REGION_ID#",',
'    pkItems: "#PK_ITEMS#",',
'    columns: "#COLUMNS#",',
'    pageItems: "#PAGE_ITEMS#",',
'    disableItems: #DISABLE_ITEMS#,',
'    multiSelectionMethod: "label"',
'  })]'';',
'  l_js := replace(l_js,''#REGION_ID#'',l_region_id);',
'  l_js := replace(l_js,''#PK_ITEMS#'',p_dynamic_action.attribute_01);',
'  l_js := replace(l_js,''#COLUMNS#'',p_dynamic_action.attribute_02);',
'  l_js := replace(l_js,''#PAGE_ITEMS#'',p_dynamic_action.attribute_03);',
'  l_js := replace(l_js,''#DISABLE_ITEMS#'',case when p_dynamic_action.attribute_05 = ''Y'' then ''true'' else ''false'' end);',
'',
'  -- Voeg subscription toe',
'  apex_javascript.add_onload_code(p_code => l_js);',
'  l_js :=',
'  q''[function() {',
'    mho.IGSetItems.setRowItems({',
'      da: this',
'    });',
'  }]'';',
'',
'  l_result.javascript_function := l_js;',
'',
'  return l_result;',
'',
'end render;'))
